import React, { useState } from 'react';
import { BookOpen, Award, TrendingUp, ChevronRight, ArrowLeft, Target } from 'lucide-react';

// ====================================================================
// 1. Navigation Komponente (Kontrast-Fix)
// ====================================================================
const Navigation = () => {
  return (
    // Hintergrund dunkler für besseren Kontrast auf hellem Hintergrund
    <nav className="bg-indigo-800/95 backdrop-blur-sm border-b border-white/30 sticky top-0 z-50 shadow-lg">
      <div className="max-w-6xl mx-auto px-4 sm:px-6 lg:px-8">
        <div className="flex justify-between items-center h-14 sm:h-16">
          <div className="flex items-center gap-2">
            <span className="text-2xl">🎓</span>
            {/* Logo bleibt sichtbar */}
            <span className="text-white font-bold text-lg sm:text-xl">
              Mathe Lernpfad
            </span>
          </div>

          <div className="flex gap-1 sm:gap-4">
            {/* Mobile: Kleinere Buttons und Icons */}
            <a
              href="/"
