// AKTUALISIERT: EfficiencyChartPlaceholder optimiert fÃ¼r Platz (weniger Text)
const EfficiencyChartPlaceholder = ({ data }) => {
    // Filtern nur die verbesserten FÃ¤cher und berechnen die Effizienz (Punkte pro Stunde)
    const efficientData = data
        .filter(d => (d.currentPoints - d.initialPoints) > 0)
        .map(d => ({
            subject: d.subject,
            improvement: d.currentPoints - d.initialPoints,
            efficiency: (d.currentPoints - d.initialPoints) / d.hours, 
        }))
        .sort((a, b) => b.efficiency - a.efficiency); 

    const maxEfficiency = Math.max(...efficientData.map(d => d.efficiency));

    return (
        <div className="mt-4 p-4 border rounded-lg bg-gray-50">
            <h5 className="font-semibold text-gray-700 mb-3">Punkte/Std. Verbesserung (Effizienz)</h5>
            <div className="space-y-3">
                {efficientData.map((item) => (
                    <div key={item.subject} className="flex items-center gap-3">
                        <span className="text-sm font-medium w-16 flex-shrink-0">{item.subject}</span> {/* Platz fÃ¼r Fachname reduzieren */}
                        <div className="flex-1 bg-gray-200 rounded-full h-6 flex items-center overflow-hidden">
                            <div 
                                className="h-full bg-blue-500 rounded-full flex items-center justify-end pr-1 text-white text-xs font-bold whitespace-nowrap" // Whitespace-nowrap + kleinere Schrift
                                style={{ width: `${(item.efficiency / maxEfficiency) * 100}%` }}
                            >
                                {/* TEXT OPTIMIERT: Nur noch Punktgewinn und Effizienz */}
                                +{item.improvement} Pkt. ({item.efficiency.toFixed(1)} Pkt./Std.)
                            </div>
                        </div>
                    </div>
                ))}
            </div>
            <p className="text-xs text-gray-500 mt-3">HÃ¶here Werte = effizientere Nachhilfe (mehr Punktgewinn pro Stunde).</p>
        </div>
    );
};

const PointsToGradeMapping = () => {
    const scale = [
        { min: 90, grade: 1.0, color: 'bg-green-500' },
        { min: 80, grade: 2.0, color: 'bg-lime-500' },
        { min: 65, grade: 3.0, color: 'bg-yellow-500' },
        { min: 50, grade: 4.0, color: 'bg-orange-500' },
        { min: 30, grade: 5.0, color: 'bg-red-500' },
        { min: 0, grade: 6.0, color: 'bg-red-700' },
    ];
    const currentPoints = mockDashboardData.tutorEffectiveness.find(d => d.subject === 'Mathematik').currentPoints;
    const maxPoints = 100;

    return (
        <div className="mt-4 p-4 border rounded-lg bg-gray-50">
            <h5 className="font-semibold text-gray-700 mb-3">Probeklausurpunkte â†’ Schulnote</h5>

            <div className="relative h-6 rounded-full bg-gray-300 mb-4 overflow-hidden">
                {scale.map((item, index) => {
                    const nextMin = scale[index - 1]?.min || maxPoints + 1;

                    return (
                        <div 
                            key={item.grade}
                            className={`h-full absolute top-0 left-0 ${item.color}`}
                            style={{ 
                                width: `${(nextMin / maxPoints) * 100}%`,
                                clipPath: `inset(0 ${(maxPoints - nextMin) / maxPoints * 100}% 0 0)`
                            }}
                        />
                    );
                })}

                <div 
                    className="absolute top-0 bottom-0 w-1.5 bg-black rounded-full shadow-lg"
                    style={{ left: `${(currentPoints / maxPoints) * 100}%`, transform: 'translateX(-50%)' }}
                ></div>
                <div 
                    className="absolute top-[-25px] text-xs font-bold whitespace-nowrap"
                    style={{ left: `${(currentPoints / maxPoints) * 100}%`, transform: 'translateX(-50%)' }}
                >
                    {currentPoints} Pkt.
                </div>
            </div>

            <div className="space-y-2">
                {scale.map(item => (
                    <div key={item.grade} className="flex justify-between items-center text-sm">
                        <div className="flex items-center gap-2">
                            <div className={`w-3 h-3 rounded-full ${item.color}`}></div>
                            <span className="font-medium text-gray-600">Note {item.grade.toFixed(1)}</span>
                        </div>
                        <span className="text-xs text-gray-500">{item.min}% bis {item.grade === 6.0 ? item.min - 1 : scale[scale.findIndex(s => s.grade === item.grade) - 1]?.min || 100}%</span>
                    </div>
                ))}
            </div>

            <p className="text-xs text-gray-500 mt-3">Der schwarze Marker zeigt den aktuellen Probeklausurstand (Mathematik) und die zugehÃ¶rige geschÃ¤tzte Schulnote.</p>
        </div>
    );
};


