import React, { useState } from 'react';
import { BookOpen, Award, TrendingUp, ChevronRight, ArrowLeft, Target } from 'lucide-react';



const Navigation = () => {
  return (
    <nav className="bg-white/10 backdrop-blur-md border-b border-white/20 sticky top-0 z-50">
      <div className="max-w-6xl mx-auto px-4 sm:px-6 lg:px-8">
        <div className="flex justify-between items-center h-14 sm:h-16">
          <div className="flex items-center gap-2">
            <span className="text-2xl">üéì</span>
            <span className="text-white font-bold text-lg sm:text-xl hidden sm:block">
              Mathe Lernpfad
            </span>
          </div>

          <div className="flex gap-2 sm:gap-4">
            <a
              href="/"
              className="px-3 py-2 sm:px-4 sm:py-2 rounded-lg text-white/90 hover:bg-white/20 transition-all font-medium text-sm sm:text-base"
            >
              üó∫Ô∏è Pfad
            </a>
            <a
              href="/character"
              className="px-3 py-2 sm:px-4 sm:py-2 rounded-lg text-white/90 hover:bg-white/20 transition-all font-medium text-sm sm:text-base"
            >
              ‚öîÔ∏è Character
            </a>
            <a
              href="/dashboard"
              className="px-3 py-2 sm:px-4 sm:py-2 rounded-lg text-white/90 hover:bg-white/20 transition-all font-medium text-sm sm:text-base"
            >
              üìä Dashboard
            </a>
          </div>
        </div>
      </div>
    </nav>
  );
};



const TutoringCharacterSheet = () => {
  const [studentName, setStudentName] = useState('Max Mustermann');
  const [selectedSubject, setSelectedSubject] = useState(null);
  const [stats, setStats] = useState({
    mathematik: 7,
    deutsch: 5,
    englisch: 8,
    physik: 6,
    chemie: 4,
    biologie: 7,
    geschichte: 6,
    geographie: 5,
    informatik: 9,
    kunst: 8,
    musik: 6,
    sport: 7
  });

  const mathSkills = {
    'Algebra & Gleichungen': {
      'Lineare Gleichungen': 8,
      'Quadratische Gleichungen': 7,
      'Gleichungssysteme': 6,
      'Ungleichungen': 5,
      'Potenzen & Wurzeln': 7
    },
    'Geometrie': {
      'Fl√§chenberechnung': 9,
      'Volumenberechnung': 8,
      'Satz des Pythagoras': 7,
      'Trigonometrie': 6,
      '√Ñhnlichkeit & Kongruenz': 7
    },
    'Funktionen': {
      'Lineare Funktionen': 8,
      'Quadratische Funktionen': 6,
      'Exponentialfunktionen': 5,
      'Funktionen zeichnen': 7,
      'Schnittpunkte berechnen': 6
    },
    'Stochastik': {
      'Wahrscheinlichkeitsrechnung': 7,
      'Kombinatorik': 6,
      'Zufallsexperimente': 8,
      'Erwartungswert': 5,
      'Bedingte Wahrscheinlichkeit': 4
    },
    'Analysis': {
      'Ableitungen': 6,
      'Kurvendiskussion': 5,
      'Extremwerte': 6,
      'Integrale': 4,
      'Wendepunkte': 5
    }
  };

  const categories = {
    'MINT-F√§cher': ['mathematik', 'physik', 'chemie', 'biologie', 'informatik'],
    'Sprachen': ['deutsch', 'englisch'],
    'Gesellschaft': ['geschichte', 'geographie'],
    'Kreativ & Sport': ['kunst', 'musik', 'sport']
  };

  const subjectLabels = {
    mathematik: 'Mathematik',
    deutsch: 'Deutsch',
    englisch: 'Englisch',
    physik: 'Physik',
    chemie: 'Chemie',
    biologie: 'Biologie',
    geschichte: 'Geschichte',
    geographie: 'Geographie',
    informatik: 'Informatik',
    kunst: 'Kunst',
    musik: 'Musik',
    sport: 'Sport'
  };

  const subjectDescriptions = {
    mathematik: 'Rechnen, Logik und Probleml√∂sung',
    deutsch: 'Sprache, Literatur und Kommunikation',
    englisch: 'Fremdsprache und internationale Kommunikation',
    physik: 'Naturgesetze und wissenschaftliches Denken',
    chemie: 'Stoffe, Reaktionen und Experimente',
    biologie: 'Leben, Natur und Organismen',
    geschichte: 'Vergangenheit und historisches Verst√§ndnis',
    geographie: 'Welt, Kulturen und R√§ume',
    informatik: 'Programmierung und digitale Kompetenzen',
    kunst: 'Kreativit√§t und visueller Ausdruck',
    musik: 'Rhythmus, Melodie und Ausdruck',
    sport: 'Fitness, Koordination und Teamgeist'
  };

  const getStatColor = (value) => {
    if (value >= 9) return 'bg-green-500';
    if (value >= 7) return 'bg-lime-400';
    if (value >= 5) return 'bg-yellow-400';
    if (value >= 3) return 'bg-orange-400';
    return 'bg-red-400';
  };

  const getGradeFromStat = (value) => {
    if (value >= 9) return '1';
    if (value >= 7) return '2';
    if (value >= 5) return '3';
    if (value >= 3) return '4';
    if (value >= 2) return '5';
    return '6';
  };

  const getLevelText = (value) => {
    if (value >= 9) return 'Meister';
    if (value >= 7) return 'Fortgeschritten';
