// Zusätzliche Icons für das Dashboard
import { Users, Clock, Zap, BarChart3 } from 'lucide-react'; 

// ====================================================================
// 3. ParentDashboard Komponente (NEU)
// ====================================================================

const DashboardCard = ({ icon, title, value, color }) => (
    <div className="bg-white p-4 sm:p-6 rounded-xl shadow-lg flex items-center justify-between">
        <div>
            <p className="text-sm font-medium text-gray-500">{title}</p>
            <h3 className="text-2xl sm:text-3xl font-bold text-gray-900 mt-1">{value}</h3>
        </div>
        <div className={`p-3 rounded-full ${color}/10`}>
            {React.cloneElement(icon, { className: `w-6 h-6 ${color}` })}
        </div>
    </div>
);

const ImprovementBar = ({ subject, tutor, initialGrade, currentGrade, improvement }) => {
    const gradeColor = (grade) => {
        if (grade <= 2) return 'text-green-600';
        if (grade <= 3) return 'text-lime-600';
        if (grade <= 4) return 'text-orange-600';
        return 'text-red-600';
    };

    return (
        <div className="mb-4 border-b pb-3 last:border-b-0">
            <div className="flex justify-between items-center mb-1">
                <h4 className="font-semibold text-gray-800">{subject}</h4>
                <div className="text-sm text-gray-600">Nachhilfelehrer: <span className="font-medium text-indigo-600">{tutor}</span></div>
            </div>
            <div className="flex items-center justify-between text-sm">
                <div className={`font-bold ${gradeColor(currentGrade)}`}>
                    Akt. Note: {currentGrade} (Vorher: {initialGrade})
                </div>
                <div className={`px-2 py-0.5 rounded-full text-xs font-bold ${improvement > 0 ? 'bg-green-100 text-green-700' : 'bg-red-100 text-red-700'}`}>
                    {improvement > 0 ? `Verbesserung: ${improvement} Note(n)` : `Keine Verbesserung`}
                </div>
            </div>
        </div>
    );
};

// Mock-Daten für das Dashboard
const mockDashboardData = {
    studentName: 'Max Mustermann',
    totalModulesCompleted: 14,
    totalTutoringHours: 25,
    averageGradeImprovement: 0.8, // Notenverbesserung (z.B. von 3.5 auf 2.7)
    currentGrades: [
        { subject: 'Mathematik', grade: 2.5, modules: 5 },
        { subject: 'Physik', grade: 3.0, modules: 3 },
        { subject: 'Englisch', grade: 1.8, modules: 6 },
        { subject: 'Chemie', grade: 4.2, modules: 0 },
    ],
    tutorEffectiveness: [
        { subject: 'Mathematik', tutor: 'Frau Schmidt', initialGrade: 3, currentGrade: 2, improvement: 1 },
        { subject: 'Physik', tutor: 'Herr Müller', initialGrade: 3, currentGrade: 3, improvement: 0 },
        { subject: 'Chemie', tutor: 'Fr. Wagner', initialGrade: 5, currentGrade: 4, improvement: 1 },
    ],
    // Alibi-Daten für Grafiken
    monthlyActivity: [5, 8, 12, 10, 15, 20], // Stunden pro Monat
    gradeTrend: [3.5, 3.2, 3.0, 2.8, 2.7, 2.5], // Notendurchschnitt über Monate
};

const ParentDashboard = () => {

    // Einfache Chart-Simulation (als Platzhalter)
    const ChartPlaceholder = ({ data, label, color, type }) => {
        const maxVal = Math.max(...data);
        const minVal = type === 'grade' ? 1 : 0; // Noten beginnen bei 1
        const range = maxVal - minVal;

        return (
            <div className="p-4 bg-white rounded-lg shadow-inner mt-4">
                <h4 className="font-bold text-gray-800 mb-3">{label}</h4>
                <div className="h-40 flex items-end">
                    {data.map((val, index) => {
                        const height = ((val - minVal) / (range || 1)) * 90 + 10; // Höhe 10% bis 100%
                        const barColor = type === 'grade' ? (val < 3 ? 'bg-green-500' : val < 4 ? 'bg-yellow-500' : 'bg-red-500') : color;

                        return (
                            <div key={index} className="flex-1 px-1 flex flex-col items-center">
                                <span className="text-xs text-gray-600 mb-1">{val}</span>
                                <div 
                                    className={`w-full rounded-t-lg ${barColor} transition-all duration-500`} 
                                    style={{ height: `${height}%` }}
                                ></div>
                            </div>
                        );
                    })}
                </div>
                <div className="flex justify-between text-xs text-gray-500 mt-2">
                    {mockDashboardData.monthlyActivity.map((_, i) => (
                         <span key={i} className="flex-1 text-center">{['Aug', 'Sep', 'Okt', 'Nov', 'Dez', 'Jan'][i]}</span>
                    ))}
                </div>
            </div>
        );
    };

    return (
        <div className="min-h-screen bg-gray-100">
            <Navigation />
            <div className="max-w-6xl mx-auto px-4 sm:px-6 lg:px-8 py-8 sm:py-10">

                {/* Dashboard Header */}
                <div className="mb-8">
                    <h1 className="text-3xl sm:text-4xl font-bold text-gray-800">Eltern-Dashboard</h1>
                    <p className="text-lg text-gray-500 mt-1">Überblick über den Fortschritt von {mockDashboardData.studentName}</p>
                </div>

                {/* KPI Cards */}
                <div className="grid grid-cols-2 md:grid-cols-4 gap-4 mb-10">
                    <DashboardCard 
                        icon={<Zap />} 
                        title="Abgeschlossene Module" 
                        value={mockDashboardData.totalModulesCompleted} 
                        color="text-indigo-600 bg-indigo-100" 
                    />
                    <DashboardCard 
                        icon={<Clock />} 
                        title="Nachhilfestunden (Gesamt)" 
                        value={`${mockDashboardData.totalTutoringHours} Std.`} 
                        color="text-blue-600 bg-blue-100" 
                    />
                    <DashboardCard 
                        icon={<BarChart3 />} 
                        title="Durchschn. Notenverbesserung" 
                        value={`${mockDashboardData.averageGradeImprovement} Noten`} 
                        color="text-green-600 bg-green-100" 
                    />
                    <DashboardCard 
                        icon={<Users />} 
                        title="Fächer mit Nachhilfe" 
                        value={mockDashboardData.tutorEffectiveness.length} 
                        color="text-yellow-600 bg-yellow-100" 
                    />
                </div>

                {/* 1. Aktuelle Noten und Modulabschluss pro Fach */}
                <div className="bg-white p-6 rounded-xl shadow-lg mb-10">
                    <h2 className="text-xl font-bold text-gray-800 mb-4 pb-2 border-b">Aktuelle Leistung pro Fach</h2>
                    <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-4 gap-4">
                        {mockDashboardData.currentGrades.map(item => (
                            <div key={item.subject} className="p-4 border rounded-lg hover:shadow-md transition-shadow">
                                <h3 className="font-bold text-lg text-gray-700">{item.subject}</h3>
                                <div className="mt-2 flex justify-between items-center">
                                    <span className="text-sm text-gray-500">Aktuelle Note:</span>
                                    <span className={`text-xl font-extrabold ${item.grade <= 3 ? 'text-green-600' : 'text-red-600'}`}>
                                        {item.grade.toFixed(1)}
                                    </span>
                                </div>
                                <div className="mt-1 flex justify-between items-center">
                                    <span className="text-sm text-gray-500">Abgeschl. Module:</span>
                                    <span className="text-lg font-bold text-indigo-500">
                                        {item.modules}
                                    </span>
                                </div>
                            </div>
                        ))}
                    </div>
                </div>

                {/* 2. Visualisierungen (Grafiken) */}
                <div className="grid grid-cols-1 lg:grid-cols-2 gap-6 mb-10">
                    <div className="bg-white p-6 rounded-xl shadow-lg">
                        <h2 className="text-xl font-bold text-gray-800">Monatliche Lernaktivität (Stunden)</h2>
                        <ChartPlaceholder 
                            data={mockDashboardData.monthlyActivity} 
                            label="Stunden" 
                            color="bg-indigo-500"
                        />
                    </div>
                    <div className="bg-white p-6 rounded-xl shadow-lg">
                        <h2 className="text-xl font-bold text-gray-800">Gesamtnote Trend (Letzte 6 Monate)</h2>
                        <ChartPlaceholder 
                            data={mockDashboardData.gradeTrend} 
                            label="Note" 
                            color="bg-green-500"
                            type="grade"
                        />
                    </div>
                </div>

                {/* 3. Nachhilfe-Effektivität */}
                <div className="bg-white p-6 rounded-xl shadow-lg">
                    <h2 className="text-xl font-bold text-gray-800 mb-4 pb-2 border-b">Effektivität der Nachhilfestunden</h2>
                    <div>
                        {mockDashboardData.tutorEffectiveness.map((item, index) => (
                            <ImprovementBar key={index} {...item} />
                        ))}
                    </div>
                    <p className="mt-4 text-sm text-gray-500">Hinweis: Die Notenverbesserung wird als Differenz zwischen der Note zu Beginn der Nachhilfe und der aktuellen Note berechnet.</p>
                </div>

            </div>
        </div>
    );
};
